#find the 13 adjacent digits in a 1000-digit number that have the largest product

def product_of_digits( string_of_digits )
  product = 1
  string_of_digits.each_char { |c| product *= c.to_i }
  return product
end

def get_largest_product_in_series( series, string_length: 13 )
  highest_product = 0
  for i in 0..series.length-(string_length+1)
    adjacent_digits = series[i,string_length]
    highest_product = [highest_product, product_of_digits(adjacent_digits)].max
  end
  return highest_product
end


series = ""

puts get_largest_product_in_series(series)
